import React, { useState, useEffect} from 'react';
import { Typeahead } from 'react-bootstrap-typeahead';
import 'bootstrap/dist/css/bootstrap.css';
import {connect} from "react-redux";
import {ApplicationState} from "../../store/rootReducer";
import {Ingredient} from "../types/types";
import {addIngredient} from "../redux/actions/actions";
import data from '../ingredientsSource/ingredients.json';
type StateProps = {
    ingredients: Ingredient[]
}

type DispatchProps = {
    addIngredient: typeof addIngredient
}
type Props = StateProps & DispatchProps;

const IngredientsSearchBar = ({ingredients, addIngredient}: Props) => {
    const [selectedItem, setSelectedItem] = useState([]); // selectedItem is used for "cleaning input"

    // const addSingleIngredient = (name) => (value: {id:number, labe:}) => 
    // {
    //     console.log(value[0].label);
        
    // }    
    return <>
            <Typeahead
                id="basic-example"
                onChange={() => {
                    setSelectedItem;
                    addIngredient({name: selectedItem[0].label, unit: selectedItem[0].unit})}}
                options={data.ingredientsList}
                placeholder="Enter ingredients!"
                selected={selectedItem}
            />
        </>;
};



export default connect(
    (state: ApplicationState) => ({
        ingredients: state.ingredients.ingredientsList,
    }), {
        addIngredient: addIngredient
    }
)(IngredientsSearchBar);
