import { removeIngredient } from "../redux/actions/actions";
import {connect} from "react-redux";

export const IngredientRow = ({id, columnIterator, label, unit}) => {

    const deleteRow = (id) =>{
        removeIngredient({
            id: id
        })
    }
    return(
        <tr key={id}>
            <td>{columnIterator}</td>
            <td>{label}</td>
            <td>
                <form>
                    <input type="number" min='0' max='1000' id={id}/>
                    {unit}
                </form>
            </td>
            <td><button type="button" className="btn btn-danger" onClick={deleteRow(id)}>X</button></td>
        </tr>
    )
}

export default connect(
    (state) => ({
        ingredients: state.ingredients.ingredientsList,
    }), {
        removeIngredient: removeIngredient
    }
)(ProductsList);
